import React, { useState } from "react";
import { Content, SideBar, Header, Footer, Student, Room, Contract, User } from "./index";
const Layout = () => {
  const [isSidebar, setIsSidebar] = useState(false);
  const [onData, setOnData] =useState(null)
  const handleOnData = (data) => {
    setOnData(data?.title)
  }
  return (
    <div className="layout">
      <SideBar isSidebar={isSidebar} onData={handleOnData} />
      <div className="layout-body">
        <Header setIsSidebar={setIsSidebar} />
      {
        onData === "HÓA ĐƠN" ? <Content /> : onData === "HỌC SINH" ? <Student/> : onData === "PHÒNG" ? <Room/> : onData === "HỢP ĐỒNG" ? <Contract/> : <User/>
      }

        <Footer />
      </div>
    </div>
  );
};

export default Layout;
